function createPixelCanvas(){for(var e=25,n=25,r=document.querySelector(".wrapper"),t=0;e>t;t++){var o=document.createElement("div");o.setAttribute("class","row"),r.appendChild(o);for(var c=0;n>c;c++){var a=document.createElement("div");a.setAttribute("class","pxBox c"+(c+1)+" r"+(t+1)),o.appendChild(a)}}}function getColors(){for(var e=document.querySelector(".colorPicker"),n=e.children,r=0;r<n.length;r++)n[r].style.background=n[r].className}function eventL(){for(var e=document.querySelector(".colorPicker"),n=e.children,r=0;r<n.length;r++)n[r].addEventListener("click",function(){currentColor=this.className})}function pxL(){for(var e=document.querySelectorAll(".pxBox"),n,r=0;r<e.length;r++)e[r].addEventListener("mousedown",function(){this.style.background=currentColor;for(var n=0;n<e.length;n++)e[n].addEventListener("mouseenter",eventHandler)});for(var r=0;r<e.length;r++)e[r].addEventListener("mouseup",function(){for(var n=0;n<e.length;n++)e[n].removeEventListener("mouseenter",eventHandler);this.style.background=currentColor})}function eventHandler(){this.style.background=currentColor}function changeColor(){var e=document.querySelector("input");e.addEventListener("change",function(){currentColor=e.value})}window.onload=function(){var e="";createPixelCanvas(),getColors(),eventL(),pxL(),changeColor()};